import React, {Component} from "react"
import basicInfo from "./FormComponent"

class Form extends Component {
    constructor() {
        super()
        this.state = {
            firstName: "",
            lastName: "",
            email: "",
            phoneNumber: ""
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event) {
        const {name, value, type, checked} = event.target
        type === "checkbox" ? 
            this.setState({
                [name]: checked
            })
        :
        this.setState({
            [name]: value
        }) 
    }

    render() {
        return(
            <basicInfo
                handleChange={this.handleChange}
                data={this.state}
            />
        )
    }
}

export default Form
