import React, {Component} from "react"
import BasicInfo from "./basicInfo"
import DisplayCV from "./Cv"

class Form extends Component {
    constructor() {
        super()
        this.state = {
            firstName: "",
            lastName: "",
            email: "",
            phoneNumber: ""
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event) {
        const {name, value, type, checked} = event.target
        type === "checkbox" ? 
            this.setState({
                [name]: checked
            })
        :
        this.setState({
            [name]: value
        }) 
    }

    render() {
        return(
            <div id='container'>
            <BasicInfo
                handleChange={this.handleChange}
                data={this.state}
            />
            <BasicInfo
                handleChange={this.handleChange}
                data={this.state}
            />
            <DisplayCV
                handleChange={this.handleChange}
                data={this.state}
            />
            </div>

        )
    }
}

export default Form
